import React from 'react';
import styled from '@emotion/styled';

const Weather = styled("div")` 
margin: 0;
text-align: left;
h3 {
  font-size: 1.7rem;
    font-weight: 900;
    text-align: center;
    color: #505865;
    position: relative;
    margin: 0 0 25px;
    line-height: 1;
    text-transform: uppercase;
}
h3 {
  margin: 0 0 10px 0;
  font-size: .65em;
  color: #1a366c;
}
ul {
  margin: 0;
  padding: 0;
}
li {
  margin-bottom: 20px;
  list-style: none;
}

p {
  margin: 0;
  font-size: .5em;
}
`

export const WeatherDetails = ({ highElevationName, lowElevationName, highElevation, lowElevation, highTemp, lowTemp, newSnowLastDay, newSnowLastTwoDay, newSnowLastWeek, snowDepth }) => {
  return (
    <Weather>
      <h2 className="line">
        <span>Today's Weather</span>
      </h2>
      <ul className="list-weather">
        <li>
          <div className="cell">
            <h3>{highElevationName}</h3>
            <p>Elevation: {highElevation}</p>
          </div>
          <div className="cell">
            <div className="weather-value">
              <span>{highTemp} {String.fromCharCode(176)}C</span>
            </div>
          </div>
        </li>
        <li>
          <h3>{lowElevationName}</h3>
          <p>Elevation: {lowElevation}</p>
        </li>
        <li className="temp">
          <span>{lowTemp} {String.fromCharCode(176)}C</span>
        </li>
      </ul>
    </Weather >
  )
}
