import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';

export const Chart = () => {
  const [data, setDate] = useState(
    {
      labels: [],
      datasets: [
        { label: "1660m", fill: false, data: littleBearSnowData },
        { label: "1220m", fill: false, data: coqSummitSnowData }
      ]
    }
  )


  return (
    <div className="chart">
      <Line
        data={data}
        options={{
          title: {
            display: true,
            text: "Snow Depth for the Coquihalla Area"
          }
        }
        } />
    </div>
  );
};
