import React, { useState } from 'react';
import styled from '@emotion/styled';
import { Doughnut } from 'react-chartjs-2';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationArrow } from '@fortawesome/free-solid-svg-icons';

const Wind = styled("div")` 
display: flex;
position: relative;
height: 100%;
flex-direction: row;
flex-wrap: wrap;
justify-content: center;
align-content: center;
background: #fff;
box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16);

.locationArrow {
  position: absolute;
  margin-top: 140px;
  transform: rotate(-45deg);
}

`

const data = {
  datasets: [{
    labels: ["Red", "Blue"],
    data: [0, 360],
    backgroundColor: [
      "rgb(255, 99, 132)",
      "rgb(54, 162, 235)",
      "rgb(255, 205, 86)"
    ]
  }]
}

const options = {

  circumference: Math.PI * 2,
  rotation: Math.PI,
  cutoutPercentage: 80, // precent
  plugins: {
    datalabels: {
      backgroundColor: 'rgba(0, 0, 0, 0.7)',
      borderColor: '#ffffff',
      align: 'start',
      anchor: 'start',
      offset: 10,
      borderRadius: 4,
      borderWidth: 1,
      formatter: function (value, context) {
        var i = context.dataIndex;
        var len = context.dataset.data.length - 1;
        if (i === len) {
          return null;
        }
        return value + ' mph';
      }
    }
  },
  legend: {
    display: false
  },
  tooltips: {
    enabled: false
  }
}

export const WindChart = () => {
  const [rotation, setRotation] = useState({
    rotate: false,
    degree: 0
  });
  console.log(rotation.degree)

  const windDirection = () => {
    const arr = [34, 45, 90, 8, 295];
    for (let i = 0; i < arr.length; i++) {
      setTimeout(setRotation({
        degree: arr[i],
      }), 2000)
    }
  }

  return (
    <Wind>
      <Doughnut data={data} options={options} />
      <FontAwesomeIcon className='locationArrow' transform={{ rotate: rotation.degree }} size='2x' color='red' icon={faLocationArrow} />
      <button onClick={() => windDirection()}>click</button>
    </Wind >
  )
}
