import React, { useState, useEffect } from 'react';

export const DropDown = () => {
  const [weatherStationData, setWeatherStationData] = useState([]);

  useEffect(() => {
    const getWeather = async () => {

      const url = `https://www.drivebc.ca/api/weather/observations/25221?format=json`;
      const response = await fetch(url);
      const data = await response.json();
      setWeatherStationData(data)
    }
    getWeather();
  }, [])
  console.log(weatherStationData)
  const station = weatherStationData && weatherStationData.station && weatherStationData.station.station;
  const stationName = station && station.name;
  const stationElevation = station && station.elevation;
  const stationTemp = station && station.airTemp;
  const slicedAirTemp = stationTemp.indexOf(" ");
  const stationSnowFall = station && station.snowEnd;
  const stationSnowDepth = station && station.snowDepth;

  return (
    <div>
      <h2>{stationName}</h2>
      <h2>{stationElevation} m</h2>
      <h2>{stationTemp}</h2>
      <h2>{stationSnowFall}</h2>
      <h2>{stationSnowDepth}</h2>
    </div>
  )
}
