import React from 'react';
import styled from '@emotion/styled';
import { Doughnut } from 'react-chartjs-2';


const Wind = styled("div")` 
display: flex;
height: 100%;
flex-direction: row;
flex-wrap: wrap;
justify-content: space-around;
align-content: center;
background: #fff;
box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16);
`

const data = {
  labels: ["Red", "Blue"],
  datasets: [{
    label: "Gauge",
    data: [0, 360],
    backgroundColor: [
      "rgb(255, 99, 132)",
      "rgb(54, 162, 235)",
      "rgb(255, 205, 86)"
    ]
  }]
}
export const WindChart = () => {
  return (
    <Wind>
      <Doughnut
        data={data}
        options={{
          title: {
            display: true,
            text: "Wind"
          }
        }
        } />
    </Wind>
  )
}
