import React from 'react';
import styled from '@emotion/styled';

const Weather = styled("div")` 
margin: 0;
text-align: left;
h3 {
  margin: 0;
  font-size: .65em;
}
ul {
  margin: 0;
  padding: 0;
}
li {
  list-style: none;
}

p {
  margin: 0;
  font-size: .5em;
}
`

export const StationDetails = ({ name, lowElevation, temp, newSnowLastDay, newSnowLastTwoDay, newSnowLastWeek, snowDepth }) => {
  return (
    <Weather>
      <ul className="list-weather">
        <li>
          <h3>{name}</h3>
          <p>Elevation: {lowElevation}</p>
        </li>
        <li className="temp">
          <h6>{temp} {String.fromCharCode(176)}C</h6>
        </li>
        <h6>24hr: {newSnowLastDay} cm</h6>
        <h6>48hr: {newSnowLastTwoDay} cm</h6>
        <h6>7 Days: {newSnowLastTwoDay} cm</h6>
        <h6>Snow Base: {snowDepth} cm</h6>
      </ul>
    </Weather >
  )
}
