import React from 'react';
import styled from '@emotion/styled';

const Station = styled("div")` 
margin-left: auto;
margin-right: auto;
max-width: 320px;
ul {
  display: block;
  list-style-type: disc;
  margin-block-start: 1em;
  margin-block-end: 1em;
  margin-inline-start: 0px;
  margin-inline-end: 0px;
  padding-inline-start: 40px;
}
.list-weather {
  margin: 0;
  padding: 0;
  text-align: left;
  list-style: none;
}
.cell {
  float: left;
  width: auto;
}
h6 {
  margin-bottom: 10px;
}
`

export const StationDetails = ({ name, elevation, temp, newSnowLastDay, newSnowLastTwoDay, newSnowLastWeek, snowDepth }) => {
  return (
    <Station>
      <ul className="list-weather">
        <li>
          <div className="cell">
            <h6>{name}</h6>
            <p>Elevation: {elevation}</p>
          </div>
        </li>
        <li>
          <h6>{temp} {String.fromCharCode(176)}C</h6>
        </li>
        <h6>24hr: {newSnowLastDay} cm</h6>
        <h6>48hr: {newSnowLastTwoDay} cm</h6>
        <h6>7 Days: {newSnowLastTwoDay} cm</h6>
        <h6>Snow Base: {snowDepth} cm</h6>
      </ul>
    </Station >
  )
}
