import React from 'react';
import styled from '@emotion/styled';

const Snow = styled("div")` 
margin: 20px auto;

h2 {
  font-size: 1.5rem;
  font-weight: 900;
  text-align: center;
  color: #505865;
  position: relative;
  margin: 0 0 25px;
  text-transform: uppercase;
}
h3 {
  margin: 0 0 10px 0;
  font-size: .65em;
  color: #1a366c;
}
ul {
  margin: 0;
  padding: 0;
}
li {
  margin-bottom: 20px;
  list-style: none;
}

p {
  margin: 0;
  font-size: .5em;
}
`

export const SnowDetails = ({ newSnowLastDayCoq, newSnowLastTwoDayCoq, newSnowLastWeek, snowDepth }) => {
  return (
    <Snow>
      <h2 className="line">
        <span>Snow Conditions</span>
      </h2>
      <h6>24hr: {newSnowLastDayCoq} cm</h6>
      <h6>48hr: {newSnowLastTwoDay} cm</h6>
      <h6>7 Days: {newSnowLastWeek} cm</h6>
      <h6>Snow Base: {snowDepth} cm</h6>

    </Snow >
  )
}
