import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import littleBearData from '../data/littleBearData.json';
import coqSummitData from '../data/coqSummitData.json';

export const Chart = () => {
  const [snowData, setSnowData] = useState({
    labels: [],
    datasets: [
      { label: "1660m", fill: false, data: [], type: "line" },
      { label: "1220m", fill: false, data: [], type: "line" }
    ]
  })
  useEffect(() => {
    const getSnowPackData = (arr) => {
      const dataArray = [];
      const dateArray = [];
      for (let i = 0; i < arr.length; i++) {
        if (arr[i].date.charAt(11) === "2" && arr[i].date.charAt(12) === "3") {
          dateArray.push(arr[i].date.slice(0, 10))
          dataArray.push(arr[i].snowpack);
        }
      }
      return { dataArray, dateArray };
    };
    const littleBearSnowData = getSnowPackData(littleBearData);
    const coqSummitSnowData = getSnowPackData(coqSummitData);

    setSnowData({
      labels: littleBearSnowData.dateArray,
      datasets: [{ data: littleBearSnowData.dataArray },
      { data: coqSummitSnowData.dataArray }
      ]
    })
  }, [])

  return (
    <div className="chart">
      <Line
        data={snowData}
        options={{
          title: {
            display: true,
            text: "Snow Depth for the Coquihalla Area"
          }, fill: false
        }
        } />
    </div>
  );
};
